script:
(function ( $ ) {
	"use strict";

	$(function () {
		var masterslider_bd7c = new MasterSlider();

		// slider controls
		masterslider_bd7c.control('arrows'     ,{ autohide:false, overVideo:true  });				masterslider_bd7c.control('bullets'    ,{ autohide:true, overVideo:true, dir:'h', align:'bottom' , margin:10  });

		masterslider_bd7c.control('circletimer',{ autohide:false, overVideo:true, color:'#434343', radius:10, stroke:3   });
		masterslider_bd7c.control('timebar'    ,{ autohide:false, overVideo:true, align:'bottom', color:'#FFFFFF'  , width:4 });
		// slider setup
		masterslider_bd7c.setup("MS60d192875bd7c", {
				width           : 800,
				height          : 480,
				minHeight       : 0,
				space           : 0,
				start           : 1,
				grabCursor      : true,
				swipe           : true,
				mouse           : true,
				layout          : "boxed",
				wheel           : false,
				autoplay        : true,
				instantStartLayers:false,
				loop            : true,
				shuffle         : false,
				preload         : 0,
				heightLimit     : true,
				autoHeight      : false,
				smoothHeight    : true,
				endPause        : false,
				overPause       : true,
				fillMode        : "fill",
				centerControls  : true,
				startOnAppear   : false,
				layersMode      : "center",
				hideLayers      : false,
				fullscreenMargin: 0,
				speed           : 2,
				dir             : "h",
				parallaxMode    : 'swipe',
				view            : "basic"
		});


		window.masterslider_instances = window.masterslider_instances || [];
		window.masterslider_instances.push( masterslider_bd7c );
	 });

})(jQuery);
